mapper.select:
SELECT id,number,password,name,sex,tel,email,img_path,state FROM
admin teacher student
WHERE state='1' AND number=#{number} AND password=#{password}

mapper.update:
UPDATE
admin teacher student
SET password=#{newPwd} WHERE number=#{number}

UPDATE
admin teacher student
SET tel=#{tel},email=#{email} WHERE number=#{number}

UPDATE
admin teacher student
SET img_path=#{img} WHERE number=#{number}
